import { motion } from "framer-motion";

const timeline = [
  {
    year: "2021 - 2024",
    title: "Grawood Assistant Supervisor â€“ Dalhousie",
    type: "Work",
    skills: ["Team Coordination", "Communication"],
    description:
      "Managed student-run restaurant ops and improved workflow efficiency.",
  },
  {
    year: "Winter 2024",
    title: "Junior Software Developer â€“ NS Health",
    type: "Project",
    skills: ["React", "Express.js", "C#", "Git"],
    description:
      "Built geriatric-focused web app with custom drug modules and REST API.",
  },
  {
    year: "Fall 2024",
    title: "QuickCash Android App â€“ Freelance",
    type: "Project",
    skills: ["Java", "Firebase", "JSON", "Android Studio"],
    description:
      "Job marketplace with real-time dashboards and live tracking.",
  },
  {
    year: "2025",
    title: "Generative AI Hackathon â€“ Dalhousie (3rd Place)",
    type: "Project",
    skills: ["Python", "Flask", "SQLAlchemy", "Ollama"],
    description:
      "Built triage app to prioritize ER patients using local LLM.",
  },
];

export default function Timeline() {
  return (
    <section
      id="timeline"
      className="scroll-mt-20 px-6 py-12 max-w-6xl mx-auto"
    >
      <h2 className="text-3xl font-bold mb-12 text-center">ðŸ“ˆ My Timeline</h2>

      <div className="relative flex flex-col gap-12 before:absolute before:left-1/2 before:top-0 before:h-full before:w-1 before:-translate-x-1/2 before:bg-blue-500 dark:before:bg-blue-400">
        {timeline.map((item, idx) => (
          <motion.div
            key={idx}
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: idx * 0.1 }}
            viewport={{ once: true }}
            className={`flex flex-col md:flex-row ${
              idx % 2 === 0 ? "md:flex-row" : "md:flex-row-reverse"
            } items-center gap-6 relative z-10`}
          >
            {/* Dot Indicator */}
            <div className="w-6 h-6 bg-blue-500 dark:bg-blue-400 rounded-full border-4 border-white dark:border-gray-900 z-20 shadow-lg"></div>

            {/* Content Card */}
            <div className="bg-white dark:bg-gray-800 shadow-md p-6 rounded-xl w-full md:w-1/2">
              <p className="text-sm text-gray-500 dark:text-gray-400 mb-1">
                {item.year}
              </p>
              <h3 className="text-lg font-semibold">{item.title}</h3>
              <p className="text-sm mt-2 mb-3">{item.description}</p>
              <div className="flex flex-wrap gap-2 text-sm">
                {item.skills.map((s, i) => (
                  <span
                    key={i}
                    className="bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 px-2 py-1 rounded-full"
                  >
                    {s}
                  </span>
                ))}
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
