export default function ThemeToggle() {
  const { theme, toggleTheme } = useTheme();

  return (
    <button
      onClick={toggleTheme}
      className="p-2 border rounded-lg bg-gray-100 dark:bg-gray-700"
    >
      {theme === "light" ? "üåô Dark" : "‚òÄÔ∏è Light"}
    </button>
  );
}
